// Get raw text
let rawText = $input.first().json.output;

// Convert escaped \n into real new lines
rawText = rawText.replace(/\\n/g, "\n");

// Split into lines
const lines = rawText.split("\n");

let result = {};

lines.forEach(line => {
    if (line.includes("=")) {
        const [key, value] = line.split("=").map(item => item.trim());
        result[key] = value;
    }
});

// Return clean structured JSON
return [
  {
    json: result
  }
];

